import { Time } from '@angular/common';
import { Injectable, OnInit } from '@angular/core';
import { AbstractControl,ValidatorFn } from '@angular/forms';
import { LocalStorageService } from './local-storage.service';
import { loginUserDetails } from '../Model/loginUserDetails';
@Injectable({
  providedIn: 'root'
})
export class CommonpropertiesService implements OnInit{
  public username: any;
  public id:any;
  public password:any;
  public hospitalId:any;
  public image: string ='data:image/png;base64,';
  loginUserDetails!:loginUserDetails;
  constructor(
    private localStorageService:LocalStorageService
  ) { }

  ngOnInit(): void {
      this.getDetails();
      this.getBackgroundImage();
  }
  
  setDetails(username:string,id:number,image:string,password:string){
    if(image==null)image=this.image;
    const loginUserDetails: loginUserDetails = {
      id: id,
      username: username,
      image: image,
      userType: password,
  };
    this.localStorageService.addDataToLocalStorage('loginUserDetails',JSON.stringify(loginUserDetails));
  }
  
  getDetails(){
    const loginUserDetails: loginUserDetails = JSON.parse(this.localStorageService.getDataFromLocalStorage('loginUserDetails'));
    this.id=loginUserDetails.id;
    this.username=loginUserDetails.username;
    this.password=loginUserDetails.userType;
    this.image=loginUserDetails.image;
  }

  clearLoginUserDetails(){
    this.localStorageService.clearDataFromLocalStorage('loginUserDetails');
    this.getDetails();
  }

  getUserName(){
    this.getDetails();
    return this.username;
  }

  getId(){
    this.getDetails();
    return this.id;
  }

  getImage(){
    this.getDetails();
    return this.image;
  }
  getPassword(){
    this.getDetails();
    return this.password;
  }
  setHospitalId(id:number){
    this.getDetails();
    this.hospitalId=id;
  }
  getBackgroundImage() : any{
    this.getDetails();
    if(this.password == '1') return 'assets/images/admin.jpg'
    else if ( this.password == '2') return 'assets/images/patient.jpg'
    else if ( this.password == '3') return 'assets/images/admin.jpg'
    return 'assets/images/admin.jpg'
  }

  PhoneNumberValidator(): ValidatorFn {
    return (control: AbstractControl): { [key: string]: any } | null => {
      const phoneNumberRegex = /^[6-9]\d{9}$/;
      if (control.value && !phoneNumberRegex.test(control.value)) {
        return { 'PhoneNumber': true };
      }
      return null;
    };
  }

  passwordValidator(): ValidatorFn {
    return (control: AbstractControl): { [key: string]: any } | null => {
      const password = control.value as string;
      const minLength = 6;
      const hasUppercase = /[A-Z]/.test(password);
      const hasLowercase = /[a-z]/.test(password);
      const hasNumber = /\d/.test(password);
      const isPasswordValid = password.length >= minLength && hasUppercase && hasLowercase && hasNumber;
      if (!isPasswordValid) {
        return { 'invalidPassword': true };
      }
      return null;
    };
  }

  nameValidator(): ValidatorFn {
    return (control: AbstractControl): { [key: string]: any } | null => {
      const nameRegex = /^[a-zA-Z\s]*$/;
      if (control.value && !nameRegex.test(control.value)) {
        return { 'invalidName': true };
      }
      return null;
    };
  }

  dobValidator():ValidatorFn{
    return (control: AbstractControl): { [key: string]: any } | null=> {
      const selectedDate = new Date(control.value);
      const currentDate = new Date();
  
      if (selectedDate >= currentDate) {
        return { 'futureDate': true };
      }
      return null;
    };
  }

  formatTime(dateTime: Time): string {
    const temp = dateTime.toString();
    const timePart = temp.split('T')[1];
    return timePart;
  }
  
}
